            )}
            {precipTileUrl && (
              <LayersControl.Overlay name="Precipitation">
                <TileLayer url={precipTileUrl} opacity={0.7} attribution='&copy; <a href="https://openweathermap.org/">OpenWeatherMap</a>' />
              </LayersControl.Overlay>
            )}
